function aga_drw()%AGA_DRW Draw the type curves of Agarwal et al. (1970)%% Syntax: aga_drw()%% Description:%   Draw a series of type curves of Agarwal et al. (1970).%% See also: aga_dim, aga_dls%figure(1)clft=logspace(0,8);for i = 0:5 for sg = 0:5:15  for rd = 1       s=aga_dls([10^i,rd,sg],t);   loglog(t,s)   hold on  end endendaxis([1e0 1e8 1e-1 1e2])xlabel('t_D','FontSize',14)ylabel('s_D','FontSize',14)figure(2)clft=logspace(0,8);for i = 0:5 for sg = 0:5:15  for rd = 10       s=aga_dls([10^i,rd,sg],t);   loglog(t,s)   hold on  end endendaxis([1e0 1e8 1e-1 1e2])xlabel('t_D','FontSize',14)ylabel('s_D','FontSize',14)figure(3)clft=logspace(0,8);for i = 0:5 for sg = 0:5:15  for rd = 100       s=aga_dls([10^i,rd,sg],t);   loglog(t,s)   hold on  end endendaxis([1e0 1e8 1e-1 1e2])xlabel('t_D','FontSize',14)ylabel('s_D','FontSize',14)figure(4)clf;t=logspace(-1,4);cde=[1;4;10;20;40;60];for i = 1:6 for rd = 1  ti=t.*10^cde(i);  [s,ds]=pca_dls([10^cde(i),rd],ti);  cd=10^cde(i)./exp(2.*2);  ti=t.*cd;  s2=aga_dls([cd,rd,2],ti);  [t2,ds2]=ldiff(ti,s2);  loglog(t,s,'-r',t,ds,'+r',t,s2,'--b',t2,ds2,'*b')  hold on; endendaxis([1e-1 1e4 1e-1 1e2])xlabel('t_D / C_D','FontSize',14)ylabel('s_D ','FontSize',14)figure(5)clf;t=logspace(-1,4);cde=[1;4;10;20;40;60];for i = 1:6 for rd = 10  ti=t.*10^cde(i);  [s,ds]=pca_dls([10^cde(i),rd],ti);  cd=10^cde(i)./exp(2.*2);  ti=t.*cd;  s2=aga_dls([cd,rd,2],ti);  [t2,ds2]=ldiff(ti,s2);  loglog(t,s,'-r',t,ds,'+r',t,s2,'--b',t2,ds2,'*b')  hold on; endendaxis([1e-1 1e4 1e-1 1e2])xlabel('t_D / C_D','FontSize',14)ylabel('s_D ','FontSize',14)figure(6)clf;t=logspace(-1,4);cde=[1;4;10;20;40;60];for i = 1:6 for rd = 100  ti=t.*10^cde(i);  [s,ds]=pca_dls([10^cde(i),rd],ti);  cd=10^cde(i)./exp(2.*2);  ti=t.*cd;  s2=aga_dls([cd,rd,2],ti);  [t2,ds2]=ldiff(ti,s2);  loglog(t,s,'-r',t,ds,'+r',t,s2,'--b',t2,ds2,'*b')  hold on; endendaxis([1e-1 1e4 1e-1 1e2])xlabel('t_D / C_D','FontSize',14)ylabel('s_D ','FontSize',14)figure(7)clft=logspace(-1,4);cde=[3;10;20;40;60];for i = 1:5 for rd = 1  ti=t.*10^cde(i);  [s,ds]=pca_dls([10^cde(i),rd],ti);  loglog(t,s,t,ds,'-.')  hold on; endendaxis([1e-1 1e4 1e-1 1e2])xlabel('t_D / C_D','FontSize',14)ylabel('s_D ','FontSize',14)figure(8)clft=logspace(-1,4);cde=[3;10;20;40;60];for i=1:5 for rd=10  ti=t.*10^cde(i);  [s,ds]=pca_dls([10^cde(i),rd],ti);  loglog(t,s,t,ds,'-.')  hold on; endendaxis([1e-1 1e4 1e-1 1e2])xlabel('t_D / C_D','FontSize',14)ylabel('s_D ','FontSize',14)figure(9)clft=logspace(-1,4);cde=[3;10;20;40;60];for i=1:5 for rd=100  ti=t.*10^cde(i);  [s,ds]=pca_dls([10^cde(i),rd],ti);  loglog(t,s,t,ds,'-.')  hold on; endendaxis([1e-1 1e4 1e-1 1e2])xlabel('t_D / C_D','FontSize',14)ylabel('s_D ','FontSize',14)figure(10)clft=logspace(-1,4);rd=1;cde=1e4t1=t.*cde;[s1,ds1]=pca_dls([cde,rd],t1);loglog(t,s1,'-r',t,ds1,'-b')hold ont=logspace(-1,4,20);cd2=1e3sg2=(log(cde)-log(cd2))./2t2=t.*cd2;s2=aga_dls([cd2,rd,sg2],t2);[td2,ds2]=ldiff(t2,s2);loglog(t,s2,'or',td2./cd2,ds2,'ob')cd3=5sg3=(log(cde)-log(cd3))./2t3=t.*cd3;s3=aga_dls([cd3,rd,sg3],t3);[td3,ds3]=ldiff(t3,s3);loglog(t,s3,'+r',td3./cd3,ds3,'+b')axis([1e-1 1e4 1e-1 1e1])xlabel('t_D / C_D','FontSize',14)ylabel('s_D ','FontSize',14)figure(11)clft=logspace(-1,4);rd=10;cde=1e4t1=t.*cde;[s1,ds1]=pca_dls([cde,rd],t1);loglog(t,s1,'-r',t,ds1,'-b')hold ont=logspace(-1,4,20);cd2=1e3sg2=(log(cde)-log(cd2))./2t2=t.*cd2;s2=aga_dls([cd2,rd,sg2],t2);[td2,ds2]=ldiff(t2,s2);loglog(t,s2,'or',td2./cd2,ds2,'ob')cd3=5sg3=(log(cde)-log(cd3))./2t3=t.*cd3;s3=aga_dls([cd3,rd,sg3],t3);[td3,ds3]=ldiff(t3,s3);loglog(t,s3,'+r',td3./cd3,ds3,'+b')axis([1e-1 1e4 1e-1 1e1])xlabel('t_D / C_D','FontSize',14)ylabel('s_D ','FontSize',14)figure(12)clft=logspace(-1,4);rd=100;cde=1e4t1=t.*cde;[s1,ds1]=pca_dls([cde,rd],t1);loglog(t,s1,'-r',t,ds1,'-b')hold ont=logspace(-1,4,20);cd2=1e3sg2=(log(cde)-log(cd2))./2t2=t.*cd2;s2=aga_dls([cd2,rd,sg2],t2);[td2,ds2]=ldiff(t2,s2);loglog(t,s2,'or',td2./cd2,ds2,'ob')cd3=5sg3=(log(cde)-log(cd3))./2t3=t.*cd3;s3=aga_dls([cd3,rd,sg3],t3);[td3,ds3]=ldiff(t3,s3);loglog(t,s3,'+r',td3./cd3,ds3,'+b')axis([1e-1 1e4 1e-1 1e1])xlabel('t_D / C_D','FontSize',14)ylabel('s_D ','FontSize',14)